								$(":submit").click(function() {
									var national_id_rec = $("#admission_form input[name='national_id']").val()
									var passport_id_rec = $("#admission_form input[name='passport_id']").val()

									if (!national_id_rec && !passport_id_rec){
										alert("Please Fill Either National ID or Passport No")
										return false;
									}
								});

								  $(":submit").click(function() {
										var father_national_id_rec = $("#admission_form input[name='father_national_id']").val()
										var father_passport_id_rec = $("#admission_form input[name='father_passport']").val()

										if (!father_national_id_rec && !father_passport_id_rec){
										alert("Please Fill Either Father National ID or Father Passport No")
										return false;
                      			  }
                   			     });

                         $(":submit").click(function() {
								var mother_national_id_rec = $("#admission_form input[name='mother_national_id']").val()
								var mother_passport_id_rec = $("#admission_form input[name='mother_passport']").val()

								if (!mother_national_id_rec && !mother_passport_id_rec){
								alert("Please Fill Either Mother National ID or Mother Passport No")
								return false;
							}
                       		 });


						$(":submit").click(function() {
								var guardian_national_id_rec = $("#admission_form input[name='guardian_national_id']").val()
								var guardian_passport_id_rec = $("#admission_form input[name='guardian_passport']").val()
								var mother_is_guardian = $("#admission_form input[name='mother_is_guardian']").is(":checked")
								var father_is_guardian = $("#admission_form input[name='father_is_guardian']").is(":checked")

								if (!mother_is_guardian && !father_is_guardian){

								if (!guardian_national_id_rec && !guardian_passport_id_rec){
								alert("Please Fill Either Guardian National ID or Guardian Passport No")
								return false;
							}}
                       		 });




								});

								$('#mother_is_guardian').click (function (){
								if($(this).prop("checked") == true){
								get_field_unrequired()
								$( "#father_is_guardian" ).prop( "disabled", true );
								}
								else if($(this).prop("checked") == false){
								get_field_required()
								$( "#father_is_guardian" ).prop( "disabled", false );
								}
								});

