								function saveupdated() {
								if(!$('#child_image').val()){
								alert('Image is required');
								}
								else if(!$('#child_first_name').val()){
								alert(' Student First Name is required');
								}
								else if(!$('#child_first_name_arabic').val()){
								alert(' Student First Name Arabic is required');
								}

								else if(!$('#child_birth_date').val()){
								alert('Birth Date is required');
								}

								else if(!$('#child_birth_place').val()){
								alert('Birth Place is required');
								}

								else if(!$('#child_national_id').val() && !$('#child_passport').val()){
								alert('Please Fill Either National ID or Passport No');
								}

								else if(!$('#nationality').val()){
								alert('Nationality is required');
								}
								else if(!$('#child_city').val()){
								alert('City is required');
								}
								else if(!$('#child_address').val()){
								alert('Address is required');
								}
								else if(!$('#child_class_grade').val()){
								alert('Grade is required');
								}
								else if(!$('#child_prevous_school').val()){
								alert('Previous School is required');
								}
								else if(!$('#child_primary_language').val()){
								alert('Primary Language is required');
								}
								else if(!$('#child_second_language').val()){
								alert('Second Language is required');
								}

								else if($("#currentRowedit").val())
								{
								var currentRowedit = $("#currentRowedit").val();
								$('#child_tabel tr').each(function(a,b){
								var row_id = $('input[name=first_name]', b).attr('id');
								if(row_id == currentRowedit){
								$("#myModal").modal('hide');
								var currentRow = $(this).closest("tr");
								currentRow.find(".gfgusername").val($('#child_first_name').val());
								currentRow.find(".gfgusermiddle_name").val($('#child_middle_name').val());
								currentRow.find(".gfchild_second_middle_name").val($('#child_second_middle_name').val());
								currentRow.find(".gfguserlast_name").val($('#child_last_name').val());

								currentRow.find(".gfgfirst_name_arabic").val($('#child_first_name_arabic').val());
								currentRow.find(".gfgmiddle_name_arabic").val($('#child_middle_name_arabic').val());
								currentRow.find(".gfgsecond_middle_name_arabic").val($('#child_second_middle_name_arabic').val());
								currentRow.find(".gfglast_name_arabic").val($('#child_last_name_arabic').val());


								currentRow.find(".gfnational_id").val($('#child_national_id').val());
								currentRow.find(".gfchild_birth_date").val($('#child_birth_date').val());
								currentRow.find(".gfchild_birth_place").val($('#child_birth_place').val());
								currentRow.find(".gfchild_class_grade").val($('#child_class_grade').val());
								currentRow.find(".gfchild_prevous_school").val($('#child_prevous_school').val());
								currentRow.find(".gfchild_city").val($('#child_city').val());
								currentRow.find(".gfchild_address").val($('#child_address').val());
								currentRow.find(".gfchild_passport").val($('#child_passport').val());

								currentRow.find(".gfchild_primary_language").val($('#child_primary_language').val());
								currentRow.find(".gfchild_second_language").val($('#child_second_language').val());
								currentRow.find(".gfchild_gender").val($('#child_gender').val());
								currentRow.find(".gfchild_religion").val($('#child_religion').val());
								currentRow.find(".gfchild_application_for_id").val($('#child_application_for_id').val());
								currentRow.find(".gfchild_sibing_status").val($('#child_sibing_status').val());


								currentRow.find(".gfnationality").val($('#nationality').val());
								$("#currentRowedit").val("");
								}

								});
								}else {
								myFunction()
								$("#myModal").modal('hide');
								}
								}


								$("#edit").live("click", function(){
								var currentRow =
								$(this).closest("tr");


								$("#currentRowedit").val(currentRow.find(".gfgusername").attr('id'));
								var first_name = currentRow.find(".gfgusername").val();
