								var child_second_middle_name_arabic =
								currentRow.find(".gfgsecond_middle_name_arabic").val();
								$('#child_second_middle_name_arabic').val(child_second_middle_name_arabic);


								var child_last_name_arabic =
								currentRow.find(".gfglast_name_arabic").val();
								$('#child_last_name_arabic').val(child_last_name_arabic);

								var child_middle_name_arabic =
								currentRow.find(".gfgmiddle_name_arabic").val();
								$('#child_middle_name_arabic').val(child_middle_name_arabic);

								var national_id =
								currentRow.find(".gfnational_id").val();
								$('#child_national_id').val(national_id);


								var birth_date =
								currentRow.find(".gfchild_birth_date").val();
								$('#child_birth_date').val(birth_date);

