								var primary_language =
								currentRow.find(".gfchild_primary_language").val();
								$('#child_primary_language').val(primary_language);

								var
								second_language =
								currentRow.find(".gfchild_second_language").val();
								$('#child_second_language').val(second_language);

								var gender =
								currentRow.find(".gfchild_gender").val();
								$('#child_gender_' + gender).prop('checked',true);

								var religion =
								currentRow.find(".gfchild_religion").val();
								$('#child_religion_' + religion).prop('checked',true);

								var application_for_id =
								currentRow.find(".gfchild_application_for_id").val();
								$('#child_application_for').val(application_for_id);

								var
								sibing_status =
								currentRow.find(".gfchild_sibing_status").val();
								$('#child_sibing_status').val(sibing_status);


								var nationality =
								currentRow.find(".gfnationality").val();
								$('#nationality').val(nationality);


								});

