								function get_child_list() {
								var ary = [];
								$(function () {
								$('#child_tabel tr').each(function
								(a, b) {

								var first_name=$('input[name=first_name]', b).val();

								var middle_name =$('input[name=middle_name]', b).val();

								var second_middle_name =$('input[name=second_middle_name]', b).val();

								var last_name =$('input[name=last_name]', b).val();

								var first_name_arabic=$('input[name=first_name_arabic]', b).val();

								var middle_name_arabic =$('input[name=middle_name_arabic]', b).val();

								var second_middle_name_arabic =$('input[name=second_middle_name_arabic]', b).val();

								var last_name_arabic =$('input[name=last_name_arabic]', b).val();

								var
								national_id
								=$('input[name=national_id]', b).val();
								var
								passport_id
								=$('input[name=passport_id]', b).val();
								var
								birth_place
								=$('input[name=birth_place]', b).val();
								var birth_date
								=$('input[name=birth_date]', b).val();
								var class_grade
								=$('input[name=class_grade]', b).val();
								var city
								=$('input[name=city]', b).val();
								var address
								=$('input[name=address]', b).val();
								var prevous_school
								=$('input[name=prevous_school]', b).val();
								var
								primary_language
								=$('input[name=primary_language]', b).val();
								var second_language
								=$('input[name=second_language]', b).val();
								var gender
								=$('input[name=gender]', b).val();
								var religion
								=$('input[name=religion]', b).val();
								var application_for_id
								=$('input[name=application_for_id]', b).val();
								var sibing_status
								=$('input[name=sibing_status]', b).val();


								var nationality
								=$('input[name=nationality]', b).val();
								var student_image
								=$('input[name=student_image]', b).val();

								ary.push({first_name:
								first_name,middle_name:middle_name,second_middle_name:second_middle_name,
								last_name:last_name,first_name_arabic:first_name_arabic,
								middle_name_arabic:middle_name_arabic,
								second_middle_name_arabic:second_middle_name_arabic,
								last_name_arabic:last_name_arabic,national_id:
								national_id,passport_id:passport_id,birth_place:birth_place,birth_date:birth_date,
								class_grade:class_grade,address:address,city:city,prevous_school:prevous_school,
								primary_language:primary_language,second_language:second_language,gender:gender,
								religion:religion,application_for_id:application_for_id,
								sibing_status:sibing_status,nationality:nationality,student_image:student_image});});
								$('#child_list').val(JSON.stringify( ary));
								});
								}
